import React from "react";
import Container from "./Container";
import Row from "./Row";
import Brian from "../images/brian.jpg";
import Peter from "../images/peter.jpeg";
import Bruce from "../images/bruce.jpeg";
import Chris from "../images/chris.jpg";
import Cleveland from "../images/cleveland.jpg";
import Consuela from "../images/consuela.png";
import Joe from "../images/joe.jpeg";
import Lois from "../images/lois.jpeg";
import Meg from "../images/meg.jpg";
import Mort from "../images/mort.jpg";
import Quagmire from "../images/quagmire.png";
import Stewie from "../images/stewie.jpg";
import Column from "./Column";
import Header from "./Header";
import Topfooter from "./Topfooter";

class Game extends React.Component {

    state = {
        count: 0,
        guessed: false,
        topScore: 0,
        imageArray: [Brian, Peter, Bruce, Chris, Cleveland, Consuela, Joe, Lois, Meg, Mort, Quagmire, Stewie],
        clickedArray: []

    };

    shuffleCards = () => {
        var array = this.state.imageArray;
        var i = array.length,
          j = 0,
          temp;

        while (i--) {
          j = Math.floor(Math.random() * (i + 1));

          temp = array[i];
          array[i] = array[j];
          array[j] = temp;
        }
      }

    gameCount = (event) => {
        console.log(event.target.id);
        clickedArray.push(event.target.id);
        this.shuffleCards();
        this.setState({ count: this.state.count + 1 });


    };

    topScoreCount = () => {

        this.setState({ topScore: this.state.topScore + 1 });

    };


    render() {

        return (
            <div>
                <Topfooter count={this.state.count}/>
                <Header />
                <Container>
                    <Row>
                        <Column img={this.state.imageArray[0]} id="i0" onClick={this.gameCount} />
                        <Column img={this.state.imageArray[1]} id="i1" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[2]} id="i2" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[3]} id="i3" onClick={this.gameCount}/>
                    </Row>
                    <Row>
                        <Column img={this.state.imageArray[4]} id="i4" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[5]} id="i5" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[6]} id="i6" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[7]} id="i7" onClick={this.gameCount}/>
                    </Row>
                    <Row>
                        <Column img={this.state.imageArray[8]} id="i8" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[9]} id="i9" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[10]} id="i10" onClick={this.gameCount}/>
                        <Column img={this.state.imageArray[11]} id="i11" onClick={this.gameCount}/>
                    </Row>
                </Container>
            </div>
        )
    }
}
export default Game;
